css:
/* Hide the print heading in the print view */
.print-heading {
    display: none;
}

/* Define height for page header and page header space */
.page-header,
.page-header-space {
    height: 50px;
}

/* Define height for page footer and page footer space */
.page-footer,
.page-footer-space {
    height: 40px;
}

/* Styling for the fixed page footer */
.page-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    margin: auto;
}

/* Apply a page break before an element with class 'page-break-before' */
.page-break-before {
    page-break-before: always;
}

/* Styling for the fixed page header */
.page-header {
    position: fixed;
    top: 0mm;
    display: block;
    width: 100%;
    border-bottom: 0 !important; /* Remove bottom border */
    color: #ffffff; /* Text color */
    margin-left: 0px !important;
    padding-left: 0 !important;
}

/* Adjust padding for print format preview in a specific context */
.print-format .print-format-preview {
    padding-left: 0px !important;
}

/* Define page size and margins for printing */
@page {
    size: A4 portrait; 
    margin: 8mm; 
    margin-bottom: 10px; 
    margin-top: 15px;
}

/* Styling for Quill Editor Table */
.ql-editor table {
    background: #111;
}

/* Ensure Quill Editor content overflows visibly */
.ql-editor {
    overflow: visible !important;
}

/* Media query for print styles */
@media print {

    .ql-editor {
        overflow: visible !important;
    }

    .ql-editor table {
        background: #111;
    }

    /* Display table header in print view */
    thead {
        display: table-header-group;
    }

    /* Display table footer in print view */
    tfoot {
        display: table-footer-group;
    }

    /* Adjust height for page header and page header space in print view */
    .page-header,
    .page-header-space {
        height: 40px !important;
        border-bottom: 0 !important; /* Remove bottom border */
    }

    /* Adjust height for page footer and page footer space in print view */
    .page-footer-space {
        height: 10px !important;
    }

    /* Adjust position of page footer image */
    .page-footer img {
        margin-top: -25px;
    }

    /* Hide the footer if the condition is met */
   .page-footer.None {
    display: none !important;
}

    /* Apply page break before specific elements in print view */
    .page-break-before {
        page-break-before: avoid;
    }

    /* Display page header in print view */
    .page-header {
        display: block;
    }

    /* Adjust padding for print format preview in print view */
    .print-format .print-format-preview {
        padding-left: 0px !important;
        padding-top: 0px !important;
    }

    /* Hide buttons in print view */
    button {
        display: none;
    }

    /* Set body margin to 0 in print view */
    body {
        margin: 0;
    }
}

/* Adjust margin for the page header */
.page-header {
    margin-top: 0px !important;
}

/* Hide a specific element in print view */
.text-center h4 {
    display: none;
}

/* Styling for table with borders in print format */
.print-format .table-bordered td {
    border: 1px solid black !important;
}

/* General padding reset for print format */
.print-format {
    padding: 0px;
}

.page-header {
    margin-bottom: 1px;
}

/* Styling for cost estimation table header */
.cost-estimation-table-header {
    color: black;
    font-weight: bold;
}

/* Styling for watermark image */
.watermark img {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 10em;
    color: rgba(0, 0, 0, 0.1);
    pointer-events: none;
    user-select: none;
    z-index: 0;
    white-space: nowrap;
    width: 35%;
    height: 100%;
    text-align: center;
    overflow: hidden;
    font-weight: bold;
}

/* Ensure watermark is fixed in print view */
@media print {
    .watermark img {
        position: fixed;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        font-size: 10em;
        color: rgba(0, 0, 0, 0.1);
        pointer-events: none;
        user-select: none;
        z-index: 0;
        white-space: nowrap;
        width: 35%;
        height: 100%;
        text-align: center;
        overflow: hidden;
    }
}

/* Content positioning to ensure proper layering */
.content {
    position: relative;
    z-index: 2;
}

/* Remove background color for tables in Quill Editor */
.ql-editor table {
    background: transparent;
}

.ql-editor table tr {
    background: transparent;
}

.ql-editor table td,
.ql-editor table th {
    background: transparent;
}

/* Styling for cover page margin */
.cover-page-margin {
    margin-top: 40px;
}

/* Styling for text color on cover page */
.cover-page-text-color p {
    color: #ff6600;
    font-size: 18px;
}

/* Styling for proposal details */
.proposal-details p {
    font-size: 16px;
}

/* Styling for offer details */
.offer-details {
    font-size: 24px;
}

/* Styling for submission details section */
.submition-details {
    margin-top: 300px;
    /*margin-top: -50px;*/
}

/* Styling for submission section data */
.submission-section-data {
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;
}

/* Set image width in the last child */
body:last-child .print-format td img {
    width: 40% !important;
}
